import 'package:flutter/material.dart';import 'package:flutter_svg/flutter_svg.dart';void main() {  runApp(const CryptoCurrenciesListApp ());}class CryptoCurrenciesListApp extends StatelessWidget {  const CryptoCurrenciesListApp ({super.key});  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'Crypto Curencies List',      theme: ThemeData(        primarySwatch: Colors.lime,        scaffoldBackgroundColor: const Color(0x3B3D3D4D),        dividerColor: Colors.white10,        appBarTheme: AppBarTheme(backgroundColor:const Color(0x3B3D3D4D),        titleTextStyle: TextStyle (color: Colors.white, fontSize: 20, fontWeight: FontWeight.w700)),        listTileTheme: const ListTileThemeData(iconColor: Colors.white ),        textTheme:  TextTheme(          bodyMedium: const TextStyle(color: Colors.white70,          fontWeight: FontWeight.w500,          fontSize: 20,          ),          labelSmall: TextStyle(color: Colors.white60,          fontWeight: FontWeight.w700,        )),        colorScheme: ColorScheme.fromSeed(seedColor: Colors.orangeAccent),        useMaterial3: true,      ),      home: const MyHomePage(title: 'Crypto Curencies List'),    );  }}class MyHomePage extends StatefulWidget {  const MyHomePage({super.key, required this.title});  final String title;  @override  State<MyHomePage> createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  int _counter = 0;  void _incrementCounter() {    setState(() {      _counter++;    });  }  @override  Widget build(BuildContext context) {    final theme = Theme.of(context);  return Scaffold(      appBar: AppBar(        title: Text(widget.title),        leading: Icon(Icons.arrow_back),      ),      body: ListView.separated(       itemCount: 10,       separatorBuilder:(context, index) => const Divider(),       itemBuilder: (context, i) => ListTile(         leading: SvgPicture.asset('assets/svg/Bitcoin.svg',height: 25, width: 25,),         title: Text(           'Bitcoin',           style: theme.textTheme.bodyMedium,         ),         subtitle: Text (           '20000\$',           style: theme.textTheme.labelSmall,         ),         trailing: const Icon(Icons.arrow_forward_ios),       ),        ),      );  }}